import styled from 'styled-components';

export const Container = styled.header`
background: -webkit-linear-gradient(left, #18174d, #ceccdb);
border:30px

    position: fixed;
    width: 100vw;
    height: 12vh;

    .logo {
        width: 25vw;
        height: 12vh;
        margin: 0 3%;
    }
`
